import React, { useState } from 'react';
import { Container, Form, Button, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [weight, setWeight] = useState('');
  const [bodyFat, setBodyFat] = useState('');
  const [leanBodyMass, setLeanBodyMass] = useState('');

  const calculateLeanBodyMass = () => {
    if (weight && bodyFat) {
      const fatMass = (bodyFat / 100) * weight;
      const leanMass = weight - fatMass;
      setLeanBodyMass(leanMass.toFixed(2));
    }
  };

  return (
    <Container className="mt-5">
      <h1 className="mb-4">Bodybuilding Calculator</h1>
      <Form>
        <Row className="mb-3">
          <Col>
            <Form.Label>Weight (in kg)</Form.Label>
            <Form.Control
              type="number"
              placeholder="Enter your weight"
              value={weight}
              onChange={(e) => setWeight(e.target.value)}
            />
          </Col>
          <Col>
            <Form.Label>Body Fat Percentage</Form.Label>
            <Form.Control
              type="number"
              placeholder="Enter your body fat percentage"
              value={bodyFat}
              onChange={(e) => setBodyFat(e.target.value)}
            />
          </Col>
        </Row>
        <Button variant="primary" onClick={calculateLeanBodyMass}>
          Calculate Lean Body Mass
        </Button>
      </Form>
      {leanBodyMass && (
        <div className="mt-4">
          <h3>Result:</h3>
          <p>Your Lean Body Mass is {leanBodyMass} kg.</p>
        </div>
      )}
    </Container>
  );
}

export default App;
